  async updateAllAnswers(companyId) {
    const forms = await this.formsRepository
      .createQueryBuilder('form')
      .where({ companyFormId: companyId })
      .leftJoinAndSelect('form.pages', 'pages')
      .select([
        'id',
        'pages.isVehicle',
        'pages.isDriver',
        'pages.isHome',
        'pages.isInsurance',
        'pages.id',
        'pages.formPageId',
      ])
      .getMany();

    await Promise.all(
      forms.map(async (form) => {
        await Promise.all(
          form['pages'].map(async (page) => {
            try {
              const questions = await this.questionsRatersRepository
                .createQueryBuilder('question')
                .where({ pageQuestionId: page.id })
                .leftJoinAndSelect('question.answers', 'answers')
                .select([
                  'id',
                  'pageQuestionId',
                  'formQuestionId',
                  'answers,objectName',
                  'answers.id',
                  'answers.questionAnswerId',
                  'answers.formAnswerId',
                  'answers.pageAnswerId',
                ])
                .getMany();

              await Promise.all(
                questions.map(async (question) => {
                  try {
                    const response = await Promise.all(
                      question.answers.map(async (answer) => {
                        try {
                          if (answer) {
                            if (!answer.objectName) {
                              if (page.isDriver) {
                                answer = await this.answersRepository.save({
                                  ...answer,
                                  ...{ objectName: 'drivers' },
                                });
                              } else if (page.isVehicle) {
                                answer = await this.answersRepository.save({
                                  ...answer,
                                  ...{ objectName: 'vehicles' },
                                });
                              } else if (page.isHome) {
                                answer = await this.answersRepository.save({
                                  ...answer,
                                  ...{ objectName: 'homes' },
                                });
                              } else if (page.isInsurance) {
                                answer = await this.answersRepository.save({
                                  ...answer,
                                  ...{ objectName: 'client' },
                                });
                              }
                            }
                            if (!question.formQuestionId) {
                              await this.questionsRatersRepository.save({
                                ...question,
                                ...{ formQuestionId: form.id },
                              });
                            }
                            if (!answer.formAnswerId) {
                              await this.answersRepository.save({
                                ...answer,
                                ...{ formAnswerId: form.id },
                              });
                            }
                            if (!answer.pageAnswerId) {
                              await this.answersRepository.save({
                                ...answer,
                                ...{ pageAnswerId: page.id },
                              });
                            }
                          }
                        } catch (error) {
                          return { status: false, error: error };
                        }
                      })
                    );
                  } catch (error) {
                    return { status: false, error: error };
                  }
                })
              );
            } catch (error) {
              return { status: false, error: error };
            }
          })
        );
      })
    );
    return { status: true, error: null };
  };
