  async getDuplicateFormObject(form, companyId) {
    const newForm = form;
    delete newForm.id;
    newForm.companyFormId = companyId;
    const pagesLg = newForm.pages.length;
    for (let pI = 0; pI < pagesLg; pI++) {
      const page = newForm.pages[pI].dataValues;
      delete page.id;
      delete page.formPageId;
      page.companyPageId = companyId;

      const questionsLg = page.questions.length;
      for (let qj = 0; qj < questionsLg; qj++) {
        const question = page.questions[qj].dataValues;
        delete question.id;
        delete question.formQuestionId;
        delete question.pageQuestionId;
        delete question.id;
        question.companyQuestionId = companyId;

        const answersLg = question.answers.length;
        for (let ak = 0; ak < answersLg; ak++) {
          const answer = question.answers[ak].dataValues;
          delete answer.id;
          delete answer.questionAnswerId;
          delete answer.formAnswerId;
          delete answer.pageAnswerId;
          delete answer.question;
          answer.companyAnswerId = companyId;

          const answerConditionsLg = answer.answerConditions
            ? answer.answerConditions.length
            : 0;
          for (let ac = 0; ac < answerConditionsLg; ac++) {
            const answerCondition = answer.answerConditions[ac].dataValues;
            delete answerCondition.id;
            delete answerCondition.pageConditionId;
            delete answerCondition.answerConditionId;
            answerCondition.companyConditionId = companyId;
          }
        }
        const questionConditionsLg = question.questionConditions
          ? question.questionConditions.length
          : 0;
        for (let qc = 0; qc < questionConditionsLg; qc++) {
          const questionCondition = question.questionConditions[qc].dataValues;
          delete questionCondition.id;
          delete questionCondition.pageConditionId;
          delete questionCondition.questionConditionId;
          questionCondition.companyConditionId = companyId;
        }
      }

      const conditionsLg = page.conditions ? page.conditions.length : 0;
      for (let pc = 0; pc < conditionsLg; pc++) {
        const pageCondition = page.conditions[pc].dataValues;
        delete pageCondition.id;
        delete pageCondition.pageConditionId;
        pageCondition.companyConditionId = companyId;
      }
    }
    return newForm;
  }
